#function1 - create the apec url list: input (2 reference numbers in format 1641XXXXX) => output ([url])

html_tables = {}
parsed_news = []
def link(start,stop):
    m=[]
    i=start
    s = list(range(start,stop))
    for i in s :
        path = 'https://cadres.apec.fr/cms/webservices/offre/public?numeroOffre=%sW' % (i)    
        m.append(path)
    return(m)
url = link(164600000, 164615495)
print(url)

#function2 - create the apec extraction: input ([url]) => output ({extraction contents})
def extraction(url):
    for i,element in enumerate(url):
        try:
            r = requests.get(url[i])
            x = r.json()
            if x["idNomTypeContrat"] == 101888 :
                html_tables[element] = x
            else:
                pass
        except ValueError:  # includes simplejson.decoder.JSONDecodeError
            pass   # do nothing!

extraction(url)


#function3 - filtrer the apec extraction: input ({extraction contents}) => output ({purified contents})
def extraction_purify(html_tables):    
       for name, item in html_tables.items():
            try:
                html_ref = item["numeroOffre"]
            except KeyError:
                pass
    
            try:
                html_title = item["intitule"]
            except KeyError:
                pass        
            
            try:
                html_company = item["nomCompteEtablissement"]
            except KeyError:
                pass
    
            try:
                html_location = item["lieux"][0]['libelleLieu'] # slicing prefecture
            except KeyError:
                pass
       
            try:
                html_postal = item["adresseOffre"]['adresseCodePostal'] # slicing prefecture
            except KeyError:
                pass       
       
            try:
                html_adresse = item["adresseOffre"]['adresseNumeroEtVoie'] # slicing prefecture
            except KeyError:
                pass  
            
            try:
                html_date_raw = item["datePublication"]
                html_date = html_date_raw[:10] # slicing date
            except KeyError:
                pass        

            try:
                html_dateprem_raw = item["datePremierePublication"]
                html_dateprem = html_dateprem_raw[:10] # slicing date
            except KeyError:
                pass
            
            try:
                html_salaire = item["salaireTexte"]
            except KeyError:
                pass
            
            try:
                html_experience = item["idNomNiveauExperience"]
            except KeyError:
                pass
            
            try:
                html_secteur = item["idNomSecteurActivite"]
            except KeyError:
                pass
            
            try:
                html_secteurparent = item["idSecteurActiviteParent"]
            except KeyError:
                pass           
 
            try:
                html_contrat = item["idNomTypeContrat"]
            except KeyError:
                pass             
            
            try:
                html_prenom = item["prenomInterlocuteur"]
            except KeyError:
                pass 
            
            try:
                html_nom = item["nomInterlocuteur"]
            except KeyError:
                pass   
                            
            try:
                html_desription_raw = item["texteHtml"]
                html_desription = BeautifulSoup(html_desription_raw, "lxml").text.replace('\r\n',' ').replace('\n',' ') # remove HTML tag from string and remove '\n' or  '\r\n'
            except KeyError:
                pass
            
            try:
                html_profil_raw = item["texteHtmlProfil"]
                html_profil = BeautifulSoup(html_profil_raw,'lxml').text.replace('\r\n',' ').replace('\n',' ')  # remove HTML tag from string and remove \n
            except KeyError:
                pass
            
            try:
                html_entreprise_raw = item["texteHtmlEntreprise"]
                html_entreprise = BeautifulSoup(html_entreprise_raw, "lxml").text.replace('\r\n',' ').replace('\n',' ')  # remove HTML tag from string and remove \n
            except KeyError:
                pass
            parsed_news.append([name,html_ref,html_title,html_company,html_location,html_postal,html_adresse,html_contrat,html_date,html_dateprem,html_salaire,html_experience,html_secteur,html_secteurparent,html_prenom,html_nom,html_desription,html_profil,html_entreprise])

extraction_purify(html_tables)

    
