#function5 - get complete list using nomenclature: input (dataframe) => output (dataframe)  
def nom(list, id, valeur):
    n=[]
    for item in list:
        n.append([item[id], item[valeur]])
    columns = ['id','value']
    return(pd.DataFrame(n, columns=columns))
 
